package com.leetcode.challenge.august2020.add_and_search_word;

import java.util.Collection;
import java.util.HashMap;
import java.util.Map;

// https://leetcode.com/explore/featured/card/august-leetcoding-challenge/549/week-1-august-1st-august-7th/3413/
class WordDictionary {

    private Container root;
    /** Initialize your data structure here. */
    public WordDictionary() {
        root = new Container();
    }

    /** Adds a word into the data structure. */
    public void addWord(String word) {
        Container temp = root;
        for (int i=0; i<word.length(); i++) {
            char c = word.charAt(i);
            if (temp.contains(c)) {
                temp = temp.get(c);
            } else {
                temp = temp.addChild(c);
            }
        }
        temp.markWord();
    }

    /** Returns if the word is in the data structure. A word could contain the dot character '.' to represent any one letter. */
    public boolean search(String word) {
        return searchHelper(root, 0, word);
    }

    private boolean searchHelper(Container start, int index, String word) {
        Container temp = start;
        for (int i=index; i<word.length(); i++) {
            char c = word.charAt(i);
            if (c == '.') {
                Collection<Container> children = temp.getChildren();
                if (children != null) {
                    for (Container child : children) {
                        if (searchHelper(child, i+1, word)) {
                            return true;
                        }
                    }
                }
                return false;
            } else {
                if (temp.contains(c)) {
                    temp = temp.get(c);
                } else {
                    return false;
                }
            }
        }
        return temp.isWord();
    }

    private static class Container {
        private boolean isWord;
        private Map<Character, Container> children; // more extensible this way, instead of char array
        Container() {
        }

        Container addChild(char c) {
            if (children == null) {
                children = new HashMap<>();
            }

            Container res = new Container();
            children.put(c, res);
            return res;
        }

        Collection<Container> getChildren() {
            if (children == null) {
                return null;
            }

            return children.values();
        }

        boolean contains(char c) {
            if (children != null) {
                return children.containsKey(c);
            }

            return false;
        }

        Container get(char c) {
            if (children != null) {
                return children.get(c);
            }

            return null;
        }

        boolean isWord() {
            return isWord;
        }

        void markWord() {
            isWord = true;
        }
    }

    public static void main(String... args) {
        WordDictionary dict = new WordDictionary();
        dict.addWord("bad");
        dict.addWord("dad");
        dict.addWord("mad");
        System.out.println(dict.search("pad"));
        System.out.println(dict.search("bad"));
        System.out.println(dict.search(".ad"));
        System.out.println(dict.search("b.."));
        System.out.println(dict.search("d"));
    }
}

